CREATE TABLE TBL_COUNTRIES (ISO3 VARCHAR(3) NOT NULL ,NAME VARCHAR(80) NOT NULL ,CONSTRAINT TBL_COUNTRIES_PK PRIMARY KEY (ISO3));
ALTER TABLE TBL_COUNTRIES ADD CONSTRAINT TBL_COUNTRIES_UK UNIQUE (NAME);
CREATE TABLE TBL_STATES (ID INTEGER NOT NULL ,NAME VARCHAR(80) NOT NULL ,COUNTRY_CODE VARCHAR(3) NOT NULL ,CONSTRAINT TBL_STATES_PK PRIMARY KEY (ID));
ALTER TABLE TBL_STATES ADD CONSTRAINT TBL_STATES_UK UNIQUE (COUNTRY_CODE,NAME);
ALTER TABLE TBL_STATES ADD CONSTRAINT TBL_STATES_country_FK FOREIGN KEY (COUNTRY_CODE) REFERENCES TBL_COUNTRIES (ISO3);